using System;using System.Collections.Generic;using System.Linq;using System.Linq.Expressions;using System.Threading.Tasks;namespace PIA.DotNet.Interview.Core.Repositories{    public interface IGenericRepository<T>    {        ICollection<T> Get();        IQueryable<T> Get(Expression<Func<T, bool>> expression);        Task<T> Get(Guid id);        Task<bool> Create(T model);        Task<bool> Update(Guid id, T Model);        Task<bool> Delete(Guid id, T model);    }}